import "./skills.css";

export default function Skills() {
  const skills = [
    { name: "Python", icon: "fa-brands fa-python", color: "#fbbf24" },
    { name: "HTML", icon: "fa-brands fa-html5", color: "#f97316" },
    { name: "CSS", icon: "fa-brands fa-css3-alt", color: "#3b82f6" },
    { name: "JavaScript", icon: "fa-brands fa-js", color: "#facc15" },
    { name: "React", icon: "fa-brands fa-react", color: "#06b6d4" },
    { name: "SQL", icon: "fa-solid fa-database", color: "#2563eb" },
    { name: "Tailwind CSS", icon: "fa-solid fa-wind", color: "#22d3ee" },
  ];

  return (
    <div className="Fullheight" id="skills">
      <div className="skills-container">
        <h1 className="skills-title">
          My <span>Skills</span>
        </h1>
        
        <div className="skills-grid">
          {skills.map((skill, index) => (
            <div key={index} className="skill-card">
              <i 
                className={`${skill.icon} skill-icon`}
                style={{ color: skill.color }}
              ></i>
              <h3 className="skill-name">{skill.name}</h3>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
